var i, l, mods = ["client", "session"];

function addGetter(mod) {
    Object.defineProperty(exports, mod, {
        get: function () {
            if (!this["_" + mod]) {
                this["_" + mod] = require("./" + mod);
            }

            return this["_" + mod];
        }
    });
}

for (i = 0, l = mods.length; i < l; ++i) {
    addGetter(mods[i]);
}
